const request = msg.payload;

const timestamp = Math.floor(Date.now() / 1000);
const timestampStr = timestamp.toString(16);
const timestampBuff = Buffer.from(timestamp.toString(16), 'hex');

const response = Buffer.alloc(23);
response.writeUInt8(0xA5, 0x00);  // msg begin

response.writeUInt8(0x0A, 0x01);    // msg length
response.writeUInt8(0x00, 0x02);

response.writeUInt8(0x10, 0x03);
response.writeUInt8(responseType(request.readUInt8(0x04)), 0x04);  // match req type

response.writeUInt8(request.readUInt8(0x05) + 0x01, 0x05,); // get from req + 1
response.writeUInt8(request.readUInt8(0x06), 0x06);  // get from req

response.writeUInt32LE(request.readUInt32LE(0x07), 0x07);  // wifi SN - get from req

response.writeUInt8(request.readUInt8(0x0B), 0x0B);  // get from req
response.writeUInt8(0x01, 0x0C);

response.writeUInt32LE(timestampBuff.readUInt32BE(0x00), 0x0D);  // timestamp

response.writeUInt8(0x78, 0x11);
response.writeUInt8(0x00, 0x12);
response.writeUInt8(0x00, 0x13);
response.writeUInt8(0x00, 0x14);

response.writeUInt8(calculateChecksum(response), 0x15);  //checksum
response.writeUInt8(0x15, 0x16);  // msg end

return { payload: response };

function responseType(requestTypeByte) {
    // node.warn(`req code: 0x${requestTypeByte.toString(16)}`);
    let responseTypeByte = requestTypeByte - (0x30);
    responseTypeByte = Math.min(Math.max(responseTypeByte, 0x00), 0xFF); //min 0x00 max 255
    // switch(requestTypeByte) {
    //     case 0x41:
    //         responseTypeByte = 0x11;
    //         break;
    //     case 0x42:
    //         responseTypeByte = 0x12;
    //         break;
    //     case 0x43:
    //         responseTypeByte = 0x13;
    //         break;
    //     case 0x47:
    //         responseTypeByte = 0x17;
    //         break;
    //     case 0x48:
    //         responseTypeByte = 0x18;
    //         break;
    //     default:
    //         responseTypeByte = 0xFF;
    // }
    // node.warn(`res code: 0x${responseTypeByte.toString(16)}`);
    return responseTypeByte;
}
function calculateChecksum(message) {
    message = message.slice(1, -2);
    let sum = 0x0;
    for (const key of message.keys()) {
        sum += message[key];
    }
    sum = sum % 256;
    // node.warn( sum.toString(16));
    return sum;
}

let buffer = msg.payload;
let data = {};
switch (buffer.length) {
    case 164:
        data.loggerSN = buffer.readUInt32LE(0x07);
        data.sensorTypeList = readSensorTypeList(buffer.slice(0x0C, 0x0E));
        data.tOperationTime = buffer.readUInt32LE(0x0E);
        data.inverterSN = (buffer.toString('utf8', 0x20, 0x30)).trimEnd();
        data.inverterTemp = buffer.readInt16LE(0x30) / 10;
        data.VDC1 = buffer.readUInt16LE(0x32) / 10;
        data.VDC2 = buffer.readUInt16LE(0x34) / 10;
        data.IDC1 = buffer.readUInt16LE(0x36) / 10;
        data.IDC2 = buffer.readUInt16LE(0x38) / 10;
        data.IAC1 = buffer.readUInt16LE(0x3A) / 10;
        data.IAC2 = buffer.readUInt16LE(0x3C) / 10;
        data.IAC3 = buffer.readUInt16LE(0x3E) / 10;
        data.VAC1 = buffer.readUInt16LE(0x40) / 10;
        data.VAC2 = buffer.readUInt16LE(0x42) / 10;
        data.VAC3 = buffer.readUInt16LE(0x44) / 10;
        data.fAC = buffer.readUInt16LE(0x46) / 100;

        data.currentPower = buffer.readUInt32LE(0x48);

        data.eToday = buffer.readUInt32LE(0x4C) / 100;
        data.eTotal = buffer.readUInt32LE(0x50) / 10;

        data.hTotal = buffer.readUInt32LE(0x54);

        data.inverterStatus = decodeInverterStatus(buffer.readUInt8(0x58));

        data.loggerTemp = buffer.readInt16LE(0x70);
        data.Vbus = buffer.readUInt16LE(0x72) / 10;
        data.VCPU1 = buffer.readUInt16LE(0x74) / 10;
        data.countdownTime = buffer.readUInt16LE(0x78);
        data.PV1insulationResistance = buffer.readUInt16LE(0x7C);
        data.PV2insulationRsistance = buffer.readUInt16LE(0x7E);
        data.catod_groundInsulationImpedance = buffer.readUInt16LE(0x80);
        data.countryCode = buffer.readUInt16LE(0x82);
        data.A_phaseDCdistribution = buffer.readUInt16LE(0x8A);
        data.B_phaseDCdistribution = buffer.readUInt16LE(0x8C);
        data.C_phaseDCdistribution = buffer.readUInt16LE(0x8E);

        data.firmware = buffer.toString('utf8', 0x90, 0x94);

        data.year = buffer.readUInt8(0x98);
        data.month = buffer.readUInt8(0x99);
        data.day = buffer.readUInt8(0x9A);
        data.hour = buffer.readUInt8(0x9B);
        data.minute = buffer.readUInt8(0x9C);
        data.second = buffer.readUInt8(0x9D);

        // node.warn(data);
        return { payload: data };
        break;
    case 99:   // hello message
        data.loggerSN = buffer.readUInt32LE(0x07);
        data.tOperationTime = buffer.readUInt32LE(0x0C);
        data.uploadingFrequency = buffer.readUInt8(0x18);
        data.dataLoggingFrequency = buffer.readUInt8(0x19);
        data.heartbeatFrequency = buffer.readUInt8(0x1A);
        data.commandType = buffer.readUInt8(0x1B);
        data.signalQuality = buffer.readUInt8(0x1C);
        data.sensorTypeNr = buffer.readUInt8(0x1D);
        data.moduleVrsion = buffer.toString('utf8', 0x1E, 0x46);
        data.macAddress = readMacAddress(buffer.slice(0x46, 0x4C));
        data.localIP = buffer.toString('utf8', 0x4C, 0x5B);
        data.sensorTypeList = readSensorTypeList(buffer.slice(0x5F, 0x61));

        // node.warn(data);
        return { payload: data };
        break;
    case 41:   // 41bytes message
        data.loggerSN = buffer.readUInt32LE(0x07);
        data.totalOperationTime = buffer.readUInt32LE(0x0C);

        // node.warn(data);
        return { payload: data };
        break;
    case 73:   // 73bytes message
        data.loggerSN = buffer.readUInt32LE(0x07);
        data.totalOperationTime = buffer.readUInt32LE(0x0C);

        // node.warn(data);
        return { payload: data };
        break;

    default:
        // node.warn(buffer.length);
        return null
        break;
}

function readMacAddress(buffer) {
    let macAddressStr = '';
    for (let buff of buffer) {
        // node.warn(buff.toString(16));
        macAddressStr += `${`0${(buff.toString(16))}`.slice(-2)}:`;
    }
    return macAddressStr.substring(0, 17);
}
function readSensorTypeList(buffer) {
    let SensorTypeListStr = `${`0${(buffer[1].toString(16))}`.slice(-2)}`;
    SensorTypeListStr += `${`0${(buffer[0].toString(16))}`.slice(-2)}`;
    return SensorTypeListStr;
}

function decodeInverterStatus(status) {
    switch (status) {
        case 0x00:
            return `standby`;
        case 0x02:
            return `normal`;
        case 0x03:
            return `fault`;
        case 0x04:
            return `permanent`;
        default:
            return `0x${`0${(status.toString(16))}`.slice(-2)}`;
    }
}
